mapper.select:
SELECT USER_UID, LOGIN_ID, EMAIL, PHONE, NAME, NOTE
FROM TAA_USER

   USER_UID = #{id}

SELECT USER_UID, LOGIN_ID, EMAIL, PHONE, NAME, NOTE
FROM ATTO_USER

    LOGIN_ID = #{id}

mapper.update:
UPDATE TAA_USER
USER_TYPE_CODE = #{userTypeCode}, EMAIL = #{email}, PHONE = #{phone}, MOBILE = #{mobile}, NAME = #{name}, NOTE = #{note}, BUILTIN_YN = #{builtinYn}, ACTIVE_YN = #{activeYn}, UPDATE_UID = #{updateUid}, UPDATE_DTM = #{updateDtm}, DISCARD_UID = #{discardUid}, DISCARD_DTM = #{discardDtm}
USER_UID = #{userUid}

UPDATE TAA_GROUP
GROUP_TYPE_CODE = #{groupTypeCode}, SVC_CODE = #{svcCode}, PARENT_UID = #{parentUid}, NAME = #{name}, NOTE = #{note}, BUILTIN_YN = #{builtinYn}, ACTIVE_YN = #{activeYn}, CREATE_UID = #{createUid}, CREATE_DTM = #{createDtm}, UPDATE_UID = #{updateUid}, UPDATE_DTM = #{updateDtm}
GROUP_UID = #{groupUid}
